files.content:
body {
    font-family: Arial, sans-serif;
    margin: 0;
    padding: 0;
    line-height: 1.6;
}

header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 10px 40px;
    background: #fff;
    border-bottom: 2px solid #ddd;
    position: sticky;
    top: 0;
    z-index: 1000;
}

header img {
    height: 50px;
}

nav a {
    text-decoration: none;
    margin: 0 10px;
    color: green;
    font-weight: bold;

}

.btn {
    background: green;
    color: white;
    padding: 10px 20px;
    text-decoration: none;
    display: inline-block;
    border-radius: 4px;
    margin-top: 10px;
}

/* Hero section */
.hero {

    padding: 50px 40px;
    display: flex;
    align-items: center;
    justify-content: space-between;
    text-align: center;
}

.hero h2{
    color: green; 
}

.hero img {
    width: 250px;
}

/* Why Choose Us section */
.about-box {
    display: flex;
    justify-content: center;
    background: white;
    padding: 30px;
     margin: 40px auto; /* Centers horizontally */
    border-radius: 10px;
    box-shadow: 0 4px 8px rgba(0,0,0,0.1);
    max-width: 1000px;
}

.about-text {
    flex: 1;
    min-width: 250px;
}

.about-text h2 {
    font-weight: bold;
    margin-bottom: 15px;
}

.about-text ul {
    list-style: disc;
    padding-left: 20px;
    margin-bottom: 20px;
    align-items: center;
}

.about-text li {
    margin-bottom: 8px;
}

.about-image {
    flex: 1;
    text-align: center;
    min-width: 200px;
}

.about-image img {
    max-width: 200px;
    height: auto;
}

/* Services section */
.services-section {
    text-align: center;
    padding: 40px 20px;
    max-width: 1100px;
    margin: auto;
}

.services-tagline {
    font-size: 14px;
    color: #555;
    margin-bottom: 5px;
}

.services-section h2 {
    color: green;
    font-weight: bold;
    margin-bottom: 30px;
}

.services-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 20px;
}

.service-card {
    border: 1px solid #ccc;
    border-radius: 6px;
    padding: 20px;
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
    background: white;
}

.service-icon img {
    height: 60px;
    margin-bottom: 15px;
}

.service-card h3 {
    font-weight: bold;
    margin-bottom: 10px;
}

.service-card p {
    font-size: 14px;
    margin: 4px 0;
}

.service-btn {
    background: #2e7d32;
    color: white;
    padding: 8px 20px;
    border-radius: 4px;
    text-decoration: none;
    margin-top: auto;
}

.service-btn.learn-more {
    background: #4caf50;
}

/* Clients marquee */
.clients {
    padding: 20px;
    overflow: hidden;
}

.clients h2 {
    text-align: center;
    color: green;
    margin-bottom: 20px;
}

.marquee {
    overflow: hidden;
    white-space: nowrap;
    position: relative;
}

.marquee-content {
    display: inline-flex;
    animation: marquee 10s linear infinite;
}

.marquee-content img {
    height: 50px;
    margin: 0 40px;
}

@keyframes marquee {
    from { transform: translateX(100%); }
    to { transform: translateX(-100%); }
}

/* Projects */
.projects-section {
    text-align: center;
    padding: 40px 20px;
    margin: auto;
}

.projects-section h2 {
    font-weight: bold;
    size: 50px;
    margin-bottom: 20px;
    color: green;
}

.projects-grid {
    display: grid;
    grid-template-columns: repeat(2, 1fr); /* 2 columns */
    gap: 20px;
}

@media (max-width: 500px) {
    .projects-grid {
        grid-template-columns: 1fr; /* 1 column for mobile */
    }
}

.project-card img {
    height: 100px; /* Reduced height */
    object-fit: cover; /* Keeps aspect ratio and crops overflow */
    display: block;
}


.project-card {
    position: relative;
    border-radius: 8px;
    overflow: hidden;
    box-shadow: 0 2px 6px rgba(0,0,0,0.1);
}

.project-card img {
    width: 100%;
    height: auto;
    display: block;
}

.project-info {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    background: linear-gradient(to top, rgba(0,0,0,0.5), transparent);
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 10px;
    color: white;
    font-size: 14px;
}

.view-btn {
    background: #2e7d32;
    color: white;
    padding: 5px 12px;
    border-radius: 4px;
    font-size: 12px;
    text-decoration: none;
}

.show-all {
    display: inline-block;
    margin-top: 20px;
    font-size: 16px;
    padding: 12px 30px;
}

/* Footer */
footer {
    background: #003300;
    color: white;
    padding: 20px;
    text-align: center;
}

footer a {
    color: white;
    text-decoration: none;
}
